mapper.select:
select t1.*, t2.user_code as getCode from user_info t1 , user_info t2
t1.user_code like concat('%',#{userCode},'%')
and t1.user_type = #{userType}
and t1.user_state = #{userState}
and t1.is_delete = 0 and t1.create_by = t2.user_id

mapper.update:
update
    user_info

    user_state = #{userState},
    update_by = #{updateBy},
    update_time = #{updateTime}


    user_id = #{userId}
